mapper.select:
select from permission where id > 0
and allow = #{allow}
and type = #{type}
and name like CONCAT('%',#{name},'%')
and target like CONCAT('%',#{target},'%')
and code like CONCAT('%',#{code},'%')

select count(id) from permission where id > 0
and allow = #{allow}
and type = #{type}
and name like CONCAT('%',#{name},'%')
and target like CONCAT('%',#{target},'%')
and code like CONCAT('%',#{code},'%')
