mapper.select:
select  from user_basic 
where status = #{status}

	and user_name like CONCAT('%',#{userName},'%')  


	and real_name like CONCAT('%',#{realName},'%')  


	and phone = #{phone}

select count(id) from user_basic 
where status = #{status} 

	and user_name like CONCAT('%',#{userName},'%')  


	and real_name like CONCAT('%',#{realName},'%')  


	and phone = #{phone}
