mapper.select:
    	SELECT  FROM tj_app_startup 
    	WHERE create_time BETWEEN #{beginTime} AND #{endTime}

    		AND device_type = #{deviceType}


		 	AND version = #{version} 


			AND channel = #{channel} 


			AND city_name = #{cityName}

		ORDER BY create_time

    	SELECT COUNT(id) FROM tj_app_startup 
    	WHERE create_time BETWEEN #{beginTime} AND #{endTime}

    		AND device_type = #{deviceType}


		 	AND version = #{version} 


			AND channel = #{channel} 


			AND city_name = #{cityName}

		ORDER BY create_time
