mapper.select:
    	SELECT CONCAT(user_id,':',device_no) AS key_clm, COUNT(id) AS value_clm FROM tj_button_click 
		WHERE create_time BETWEEN #{beginTime} AND #{endTime} 
		AND button_name IN(

		#{item}
		 )	
		GROUP BY CONCAT(user_id,':',device_no)
		ORDER BY create_time

    	SELECT DATE_FORMAT(create_time,'%Y%m%d') AS key_clm, COUNT(id) AS value_clm FROM tj_button_click 
		WHERE create_time BETWEEN #{beginTime} AND #{endTime} 
		AND button_name IN(

		#{item}
		 )	
		GROUP BY DATE_FORMAT(create_time,'%Y%m%d')
		ORDER BY create_time
